sys_script_client.script:
function onLoad() {

    if (!g_form.isNewRecord()) {
        if (g_form.getValue("genehmigt") != '1') {

            g_form.setReadOnly('beleg', true);
            g_form.setReadOnly('antrag', true);
            g_form.setReadOnly('betrag', true);
            g_form.setReadOnly('kostenart', true);
            g_form.setReadOnly('mitarbeiter', true);
            g_form.setReadOnly('kostenantragsnummer', true);
        }


        if (g_user.userName != g_form.getValue("sys_created_by")) {
            g_form.setReadOnly('beleg', true);
            g_form.setReadOnly('antrag', true);
            g_form.setReadOnly('betrag', true);
            g_form.setReadOnly('kostenart', true);
            g_form.setReadOnly('mitarbeiter', true);
            g_form.setReadOnly('kostenantragsnummer', true);
        }
    }

}
