sys_script_client.script:
function onLoad() {
    //Type appropriate comment here, and begin script below
   if (!g_form.isNewRecord()) {
        if (g_form.getValue("genehmigt") != '1') {

            g_form.setReadOnly('bahnreise', true);
            g_form.setReadOnly('bemerkungen', true);
            g_form.setReadOnly('flug_von', true);
            g_form.setReadOnly('hinreise', true);
            g_form.setReadOnly('ma_id', true);
            g_form.setReadOnly('hotel', true);
			g_form.setReadOnly('jobticket', true);
			g_form.setReadOnly('antragsnummer', true);
			g_form.setReadOnly('ort', true);
			g_form.setReadOnly('rueckreise', true);
			g_form.setReadOnly('mit_privatem_pkw_gefahrene_distanz', true);
			g_form.setReadOnly('verantwortlicher_leiter', true);
        }


        if (g_user.userName != g_form.getValue("sys_created_by")) {
            g_form.setReadOnly('bahnreise', true);
            g_form.setReadOnly('bemerkungen', true);
            g_form.setReadOnly('flug_von', true);
            g_form.setReadOnly('hinreise', true);
            g_form.setReadOnly('mitarbeiter', true);
            g_form.setReadOnly('hotel', true);
			g_form.setReadOnly('jobticket', true);
			g_form.setReadOnly('antragsnummer', true);
			g_form.setReadOnly('ort', true);
			g_form.setReadOnly('rueckreise', true);
			g_form.setReadOnly('mit_privatem_pkw_gefahrene_distanz', true);
			g_form.setReadOnly('verantwortlicher_leiter', true);
        }
    }
}
